import React from 'react';
import './index.css';

const App = () => {

    let array = [];
    let value;

    const gettingValue = (event) => {
       value = event.target.value;
    }

    const fetch = () => {
        array.push(value);
    }
    return(
        <>
            <div className="container">
                <h3>Todo List App</h3>
                <br />
                <div className="inputSpace">
                    <input type="text" placeholder='' onChange={gettingValue}/>
                    <button onClick={fetch}>Add</button> 
                </div><br />
                <hr />
                {array.map((val)=>{
                    return <div className="answer">
                        <h4>{val}</h4>
                    </div>;
                })}
            </div>
        </>
    );

};

export default App;
