import React ,{useState}from 'react';
import './index.css';

const App = () => {

    // const [temp,settemp] = useState();
    let array = [];
    let value;
    

    const gettingValue = (event) => {
      value=event.target.value;
    }

    const fetch = () => {
        // settemp(value);
       
        // 
        if(value === undefined){
            alert('Please enter the value')
        }else{
        array.push(value);
        }
        console.log(array);
       
    }
    return(
        <>
            <div className="container">
                <h3>Todo List App</h3>
                <br />
                <div className="inputSpace">
                    <input type="text" placeholder='' onChange={gettingValue}/>
                    <button onClick={fetch}>Add</button> 
                </div><br />
                <hr />
                
            </div>
        </>
    );

};

export default App;
